                    <span class="hljs-keyword">foreach</span> (<span class="hljs-keyword">var</span> coefficient <span class="hljs-keyword">in</span> row)
                    {
                        <span class="hljs-keyword">yield</span> <span class="hljs-keyword">return</span> coefficient;
