                <span class="hljs-keyword">return</span> <span class="hljs-keyword">new</span> BigInteger(<span class="hljs-number">0</span>);
            }

            <span class="hljs-keyword">return</span> BinomialCoefficient(n - <span class="hljs-number">1</span>, k) + BinomialCoefficient(n - <span class="hljs-number">1</span>, k - <span class="hljs-number">1</span>);
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> IEnumerable&lt;BigInteger&gt; <span class="hljs-title">GenerateRow</span>(<span class="hljs-params"><span class="hljs-built_in">long</span> n</span>)</span>
