    <span class="hljs-comment"><span class="hljs-doctag">///</span>     Cocktail Sort is a variation of Bubble sort, where Cocktail</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span>     Sort traverses through a given array in both directions alternatively.</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;typeparam name=&quot;T&quot;&gt;</span>Array input type.<span class="hljs-doctag">&lt;/typeparam&gt;</span></span>
    <span class="hljs-keyword">public</span> <span class="hljs-keyword">class</span> <span class="hljs-title">CocktailSorter</span>&lt;<span class="hljs-title">T</span>&gt; : <span class="hljs-title">IComparisonSorter</span>&lt;<span class="hljs-title">T</span>&gt;
    {
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     Sorts array using Cocktail sort algorithm.</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;array&quot;&gt;</span>Input array.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;comparer&quot;&gt;</span>Type of comparer for array elements.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-function"><span class="hljs-keyword">public</span> <span class="hljs-keyword">void</span> <span class="hljs-title">Sort</span>(<span class="hljs-params">T[] array, IComparer&lt;T&gt; comparer</span>)</span> =&gt; CocktailSort(array, comparer);

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> <span class="hljs-keyword">void</span> <span class="hljs-title">CocktailSort</span>(<span class="hljs-params">IList&lt;T&gt; array, IComparer&lt;T&gt; comparer</span>)</span>
        {
            <span class="hljs-keyword">var</span> swapped = <span class="hljs-literal">true</span>;

            <span class="hljs-keyword">var</span> startIndex = <span class="hljs-number">0</span>;
            <span class="hljs-keyword">var</span> endIndex = array.Count - <span class="hljs-number">1</span>;

            <span class="hljs-keyword">while</span> (swapped)
            {
                <span class="hljs-keyword">for</span> (<span class="hljs-keyword">var</span> i = startIndex; i &lt; endIndex; i++)
                {
                    <span class="hljs-keyword">if</span> (comparer.Compare(array[i], array[i + <span class="hljs-number">1</span>]) != <span class="hljs-number">1</span>)
                    {
                        <span class="hljs-keyword">continue</span>;
                    }

                    <span class="hljs-keyword">var</span> highValue = array[i];
                    array[i] = array[i + <span class="hljs-number">1</span>];
                    array[i + <span class="hljs-number">1</span>] = highValue;
                }

                endIndex--;
                swapped = <span class="hljs-literal">false</span>;

                <span class="hljs-keyword">for</span> (<span class="hljs-keyword">var</span> i = endIndex; i &gt; startIndex; i--)
                {
                    <span class="hljs-keyword">if</span> (comparer.Compare(array[i], array[i - <span class="hljs-number">1</span>]) != <span class="hljs-number">-1</span>)
                    {
                        <span class="hljs-keyword">continue</span>;
                    }

                    <span class="hljs-keyword">var</span> highValue = array[i];
                    array[i] = array[i - <span class="hljs-number">1</span>];
                    array[i - <span class="hljs-number">1</span>] = highValue;

                    swapped = <span class="hljs-literal">true</span>;
                }

                startIndex++;
            }
        }
    }
}
