        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> T[] <span class="hljs-title">GetOptimalItems</span>(<span class="hljs-params">T[] items, Func&lt;T, <span class="hljs-built_in">int</span>&gt; weightSelector, <span class="hljs-built_in">double</span>[,] cache, <span class="hljs-built_in">int</span> capacity</span>)</span>
