﻿<span class="hljs-keyword">using</span> System.Collections.Generic;

<span class="hljs-keyword">namespace</span> <span class="hljs-title">Algorithms.Sorters.Comparison</span>
{
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;summary&gt;</span></span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span>     Heap sort is a comparison based sorting technique</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span>     based on Binary Heap data structure.</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;typeparam name=&quot;T&quot;&gt;</span>Input array type.<span class="hljs-doctag">&lt;/typeparam&gt;</span></span>
    <span class="hljs-keyword">public</span> <span class="hljs-keyword">class</span> <span class="hljs-title">HeapSorter</span>&lt;<span class="hljs-title">T</span>&gt; : <span class="hljs-title">IComparisonSorter</span>&lt;<span class="hljs-title">T</span>&gt;
    {
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     Sorts input array using heap sort algorithm.</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;array&quot;&gt;</span>Input array.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;comparer&quot;&gt;</span>Comparer type for elements.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-function"><span class="hljs-keyword">public</span> <span class="hljs-keyword">void</span> <span class="hljs-title">Sort</span>(<span class="hljs-params">T[] array, IComparer&lt;T&gt; comparer</span>)</span> =&gt; HeapSort(array, comparer);

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> <span class="hljs-keyword">void</span> <span class="hljs-title">HeapSort</span>(<span class="hljs-params">IList&lt;T&gt; data, IComparer&lt;T&gt; comparer</span>)</span>
        {
            <span class="hljs-keyword">var</span> heapSize = data.Count;
            <span class="hljs-keyword">for</span> (<span class="hljs-keyword">var</span> p = (heapSize - <span class="hljs-number">1</span>) / <span class="hljs-number">2</span>; p &gt;= <span class="hljs-number">0</span>; p--)
            {
                MakeHeap(data, heapSize, p, comparer);
            }

            <span class="hljs-keyword">for</span> (<span class="hljs-keyword">var</span> i = data.Count - <span class="hljs-number">1</span>; i &gt; <span class="hljs-number">0</span>; i--)
            {
                <span class="hljs-keyword">var</span> temp = data[i];
                data[i] = data[<span class="hljs-number">0</span>];
                data[<span class="hljs-number">0</span>] = temp;

                heapSize--;
                MakeHeap(data, heapSize, <span class="hljs-number">0</span>, comparer);
            }
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> <span class="hljs-keyword">void</span> <span class="hljs-title">MakeHeap</span>(<span class="hljs-params">IList&lt;T&gt; input, <span class="hljs-built_in">int</span> heapSize, <span class="hljs-built_in">int</span> index, IComparer&lt;T&gt; comparer</span>)</span>
        {
            <span class="hljs-keyword">var</span> rIndex = index;

            <span class="hljs-keyword">while</span> (<span class="hljs-literal">true</span>)
            {
                <span class="hljs-keyword">var</span> left = (rIndex + <span class="hljs-number">1</span>) * <span class="hljs-number">2</span> - <span class="hljs-number">1</span>;
                <span class="hljs-keyword">var</span> right = (rIndex + <span class="hljs-number">1</span>) * <span class="hljs-number">2</span>;
                <span class="hljs-keyword">var</span> largest = left &lt; heapSize &amp;&amp; comparer.Compare(input[left], input[rIndex]) == <span class="hljs-number">1</span> ? left : rIndex;

                <span class="hljs-comment">// finds the index of the largest</span>
                <span class="hljs-keyword">if</span> (right &lt; heapSize &amp;&amp; comparer.Compare(input[right], input[largest]) == <span class="hljs-number">1</span>)
                {
                    largest = right;
                }

                <span class="hljs-keyword">if</span> (largest == rIndex)
                {
                    <span class="hljs-keyword">return</span>;
                }

                <span class="hljs-comment">// process of reheaping / swapping</span>
                <span class="hljs-keyword">var</span> temp = input[rIndex];
                input[rIndex] = input[largest];
                input[largest] = temp;

                rIndex = largest;
            }
        }
    }
}
