ï»¿<span class="hljs-keyword">using</span> System.Collections.Generic;
<span class="hljs-keyword">using</span> System.Linq;

<span class="hljs-keyword">namespace</span> <span class="hljs-title">DataStructures</span>
{
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;summary&gt;</span></span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> Inverted index is the simplest form of document indexing,</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> allowing performing boolean queries on text data.</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span></span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> This realization is just simplified for better understanding the process of indexing</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> and working on straightforward string inputs.</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
    <span class="hljs-keyword">public</span> <span class="hljs-keyword">class</span> <span class="hljs-title">InvertedIndex</span>
    {
        <span class="hljs-keyword">private</span> <span class="hljs-keyword">readonly</span> Dictionary&lt;<span class="hljs-built_in">string</span>, List&lt;<span class="hljs-built_in">string</span>&gt;&gt; invertedIndex = <span class="hljs-keyword">new</span>();

        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> Build inverted index with source name and source content.</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;sourceName&quot;&gt;</span>Name of the source.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;sourceContent&quot;&gt;</span>Content of the source.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-function"><span class="hljs-keyword">public</span> <span class="hljs-keyword">void</span> <span class="hljs-title">AddToIndex</span>(<span class="hljs-params"><span class="hljs-built_in">string</span> sourceName, <span class="hljs-built_in">string</span> sourceContent</span>)</span>
        {
            <span class="hljs-keyword">var</span> context = sourceContent.Split(<span class="hljs-string">&#x27; &#x27;</span>).Distinct();
            <span class="hljs-keyword">foreach</span> (<span class="hljs-keyword">var</span> word <span class="hljs-keyword">in</span> context)
            {
                <span class="hljs-keyword">if</span> (!invertedIndex.ContainsKey(word))
                {
                    invertedIndex.Add(word, <span class="hljs-keyword">new</span> List&lt;<span class="hljs-built_in">string</span>&gt; { sourceName });
                }
                <span class="hljs-keyword">else</span>
                {
                    invertedIndex[word].Add(sourceName);
                }
            }
        }

        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> Returns the source names contains ALL terms inside at same time.</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;terms&quot;&gt;</span>List of terms.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;returns&gt;</span>Source names.<span class="hljs-doctag">&lt;/returns&gt;</span></span>
        <span class="hljs-function"><span class="hljs-keyword">public</span> IEnumerable&lt;<span class="hljs-built_in">string</span>&gt; <span class="hljs-title">And</span>(<span class="hljs-params">IEnumerable&lt;<span class="hljs-built_in">string</span>&gt; terms</span>)</span>
        {
            <span class="hljs-keyword">var</span> entries = terms
                .Select(term =&gt; invertedIndex
                    .Where(x =&gt; x.Key.Equals(term))
                    .SelectMany(x =&gt; x.Value))
                .ToList();

            <span class="hljs-keyword">var</span> intersection = entries
                .Skip(<span class="hljs-number">1</span>)
                .Aggregate(<span class="hljs-keyword">new</span> HashSet&lt;<span class="hljs-built_in">string</span>&gt;(entries.First()), (hashSet, enumerable) =&gt;
                {
                    hashSet.IntersectWith(enumerable);
                    <span class="hljs-keyword">return</span> hashSet;
                });

            <span class="hljs-keyword">return</span> intersection;
        }

        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> Returns the source names contains AT LEAST ONE from terms inside.</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;terms&quot;&gt;</span>List of terms.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;returns&gt;</span>Source names.<span class="hljs-doctag">&lt;/returns&gt;</span></span>
        <span class="hljs-function"><span class="hljs-keyword">public</span> IEnumerable&lt;<span class="hljs-built_in">string</span>&gt; <span class="hljs-title">Or</span>(<span class="hljs-params">IEnumerable&lt;<span class="hljs-built_in">string</span>&gt; terms</span>)</span>
        {
            <span class="hljs-keyword">var</span> sources = <span class="hljs-keyword">new</span> List&lt;<span class="hljs-built_in">string</span>&gt;();
            <span class="hljs-keyword">foreach</span> (<span class="hljs-keyword">var</span> term <span class="hljs-keyword">in</span> terms)
            {
                <span class="hljs-keyword">var</span> source = invertedIndex
                    .Where(x =&gt; x.Key.Equals(term))
                    .SelectMany(x =&gt; x.Value);

                sources.AddRange(source);
            }

            <span class="hljs-keyword">return</span> sources.Distinct();
        }
    }
}
