            <span class="hljs-keyword">var</span> numberOfDigits = <span class="hljs-number">0</span>;
            <span class="hljs-keyword">while</span> (temp != <span class="hljs-number">0</span>)
            {
                numberOfDigits++;
                temp /= <span class="hljs-number">10</span>;
            }

            temp = number;
            <span class="hljs-keyword">while</span> (number &gt; <span class="hljs-number">0</span>)
